import { View, Text, FlatList, TouchableOpacity, TextInput, StyleSheet } from 'react-native'
import React, {Component} from 'react'
import { db, auth } from '../../firebase/config'
import firebase from 'firebase'

class Comments extends Component {
    constructor(props){
        super(props)
        this.state={
            comentarios:[],
            nuevoComentario:''
        } 
    }

    componentDidMount(){
        const idDoc = this.props.route.params.id
// console.log(idDoc)
            db.collection('messages').doc(idDoc).onSnapshot(doc => {
                console.log(doc)
            })
            .then()
            .catch(error => console.log(error))
    }

    onSubmit(elComentario){
        const comment ={
            owner: auth.currentUser.email,
            createdAt: Date.now(),
            description: elComentario
        }

        if(elComentario !== ''){
            db
            .collection('messages')
            .doc(this.props.route.params.id)
            .update({
                subMessages:firebase.firestore.FieldValue.arrayUnion(comment)
            })
            .then(response => this.setState({nuevoComentario:''}))
            .catch(error => console.log(error))
        }

    }

    render(){
        return (
          <View>
            <FlatList
            data={this.state.comentarios}
            keyExtractor={( item ) => item.createdAt.toString()}
            renderItem={ ( {item} ) => <Text>{item.description}</Text>}
            />
            <View>
                <TextInput
                placeholder='Agrega tu comentario'
                onChangeText={(text) => this.setState({nuevoComentario : text})}
                value={this.state.nuevoComentario}
                keyboardType='default'
                style={styles.inputComment}
                />
                <TouchableOpacity 
                onPress={()=> this.onSubmit(this.state.nuevoComentario)}
                style={styles.btnComment}
                >
                    <Text>Enviar</Text>
                </TouchableOpacity>
            </View>
          </View>
        )
    }
}

const styles = StyleSheet.create({
    container:{
      width:'100%',
      height:'100%',
      justifyContent:'center',
      alignItems:'center'
    },
    containerSubmessage:{
      flexDirection:'row',
      width:'90%'
    },
    inputComment:{
      borderWidth:1,
      backgroundColor:'#c3c3c3',
      width:'80%'
    },
    btnComment:{
      width:'20%',
      padding:10,
      backgroundColor:'#d3d3d3'

    }
  })

export default Comments
