import { View, Text, StyleSheet } from 'react-native'
import React, {Component} from 'react'
import { FontAwesome } from '@expo/vector-icons'
import firebase from 'firebase'
import {auth} from '../../firebase/config'


class Message extends Component {

    constructor(props){
        super(props)
        this.state={
            likes:0,
            miLike:false,
            arrLikes:[],
            arrSubMessages:[]
        }
    }

    componentDidMount(){
        console.log('Este es el metodo de Firebase')
        console.log(firebase.firestore.FieldValue)
        const documento = this.props.info.data
        const tieneMiLike = documento.likes.includes(auth.currentUser.email)
        if(documento.likes){
            let cantActualLikes = documento.likes.length
            this.setState({
                likes:cantActualLikes
            })
        }
        if(tieneMiLike){
            this.setState({
                miLike: true
            })
        }
    }


    render(){
        return (
            <View style={styles.container}>
                <View>
                    <Text>{this.props.info.data.message}</Text>
                </View>
                <View>
                    {
                        this.state.myLike 
                        ? <FontAwesome name='heart' size={24} color='red'/> 
                        : <FontAwesome name='heart-o' size={24} color='black' /> 

                    }
                </View>
            </View>
        )
    }
}

const styles= StyleSheet.create({
    container:{
        flex:1,
        flexDirection:'row',
        justifyContent:'space-between',
        paddingVertical:8,
        paddingHorizontal:6,
    }
})

export default Message
