import { View, Text, TextInput, TouchableOpacity, FlatList, StyleSheet } from 'react-native'
import React, {Component} from 'react'
import {auth, db} from '../../firebase/config'
import firebase from 'firebase'


class Submessages extends Component {
  constructor(props){
    super(props)
    this.state={
      allSubmessages:[],
      newSubmessage:'',
    }
  }

  componentDidMount(){
    console.log(this.props)
    const {docId} = this.props.route.params
    db.collection('messages').doc(docId).onSnapshot(
      doc => {
        // console.log(doc.data())
        this.setState({
          allSubmessages: doc.data().subMessages
        })
      }
    )
  }
  enviarSubmessage(submessageText){
    const {docId} = this.props.route.params
    if(this.state.newSubmessage === ''){
      return ''
    }
    const newSubmessage = {
      owner: auth.currentUser.email,
      submessageText: submessageText,
      createdAt: Date.now()
    }
    db.collection('messages').doc(docId).update({
      subMessages: firebase.firestore.FieldValue.arrayUnion(newSubmessage)
    })
    .then()


  }
  render(){
    return (
      <View style={styles.container}>
        <FlatList
        data={this.state.allSubmessages}
        keyExtractor={item => item.id.toString()}
        renderItem={({item})=><Text>Hola</Text>}
        />
        <View style={styles.containerSubmessage}>
          <TextInput
          placeholder='Agrega tu Submessage'
          onChangeText={text => this.setState({newSubmessage: text})}
          value={this.state.newSubmessage}
          style={styles.inputSubmessage}
          />
          <TouchableOpacity
          onPress={()=> this.enviarSubmessage(this.state.newSubmessage)}
          style={styles.btnSubmessage}
          >
            <Text>Enviar</Text>
          </TouchableOpacity>
        </View>
      </View>
    )
  }
}
const styles = StyleSheet.create({
  container:{
    width:'100%',
    height:'100%',
    justifyContent:'center',
    alignItems:'center'
  },
  containerSubmessage:{
    flexDirection:'row',
    width:'90%'
  },
  inputSubmessage:{
    borderWidth:1,
    backgroundColor:'#c3c3c3',
    width:'80%'
  },
  btnSubmessage:{
    width:'20%',
    padding:10,
    backgroundColor:'#d3d3d3'

  }
})
export default Submessages
