import { View, Text, TextInput, TouchableOpacity, FlatList } from 'react-native'
import React, {Component} from 'react'
import {db} from '../../firebase/config'
class Submessages extends Component {
  constructor(props){
    super(props)
    this.state={
      allSubmessages:[],
      newSubmessage:'',
    }
  }

  componentDidMount(){
    // console.log
    const docId = this.props.route.params
    db.collection('messages').doc(docId).onSnapshot(
      doc => {
        console.log(doc)
      }
    )
  }

  render(){
    return (
      <View>
        <FlatList
        data={this.state.allSubmessages}
        keyExtractor={item => item.id.toString()}
        renderItem={({item})=><Text>Hola</Text>}
        />
        <View>
          <TextInput />
          <TouchableOpacity>
            <Text>Enviar</Text>
          </TouchableOpacity>
        </View>
      </View>
    )
  }
}

export default Submessages
