import { View, Text, TextInput, TouchableOpacity, FlatList, StyleSheet } from 'react-native'
import React, {Component} from 'react'
import {db} from '../../firebase/config'
class Submessages extends Component {
  constructor(props){
    super(props)
    this.state={
      allSubmessages:[],
      newSubmessage:'',
    }
  }

  componentDidMount(){
    // console.log
    const docId = this.props.route.params
    db.collection('messages').doc(docId).onSnapshot(
      doc => {
        console.log(doc.data())
      }
    )
  }
  enviarSubmessage(){

  }
  render(){
    return (
      <View>
        <FlatList
        data={this.state.allSubmessages}
        keyExtractor={item => item.id.toString()}
        renderItem={({item})=><Text>Hola</Text>}
        />
        <View style={styles.containerSubmessage}>
          <TextInput
          placeholder='Agrega tu Submessage'
          onChangeText={text => this.setState({newSubmessage: text})}
          value={this.state.newSubmessage}
          style={styles.inputSubmessage}
          />
          <TouchableOpacity
          onPress={()=> this.enviarSubmessage(this.state.newSubmessage)}
          style={styles.btnSubmessage}
          >
            <Text>Enviar</Text>
          </TouchableOpacity>
        </View>
      </View>
    )
  }
}
const styles = StyleSheet.create({
  containerSubmessage:{
    flexDirection:'row'
  },
  inputSubmessage:{
    borderWidth:1,
    backgroundColor:'#c3c3c3',
    width:'70%'
  },
  btnSubmessage:{
    padding:10,
    backgroundColor:'#d3d3d3'
  }
})
export default Submessages
