import React, { Component } from 'react'

export default class MyCamera extends Component {
    constructor(props){
        super(props)
        this.state={
            prueba:''
        }
    }
    componentDidMount(){
        this.setState({prueba:'hola2'}, ()=> console.log(prueba))
    }
  render() {
    return (
      <div>index</div>
    )
  }
}
