import React, {Component} from 'react'
import {View, Text, TouchableOpacity, StyleSheet, Image} from 'react-native'
import {Camera} from 'expo-camera'

class MyCamera extends Component {
    constructor(props){
        super(props)
        this.state = {
            permisos: false,
            urlFoto:'',
            mostrarCamara: true
        }
        this.metodosDeCamara = ''
    }

    componentDidMount(){
        this.setState({permisos:true})
        Camera.requestCameraPermissionsAsync()
        .then(()=>{
            console.log('antes de fallar')
            // this.setState({permisos: true})
        })
        .catch(error => console.log(error))
        console.log(Camera)
    }

    tomaLaFoto(){
        console.log('Digan Whisky!')
    }

    render(){
        return(
            <View>
                {
                    this.state.permisos ?
                        this.state.mostrarCamara === false ?
                        <Text>Aqui vamos a renderizar la imagen</Text>
                        :
                        <View style={styles.container}>
                            <Camera
                                styles={styles.camara}
                                type={Camera.Constants.Type.back}
                                ref={ metodos => this.metodosDeCamara = metodos}
                            />
                            <TouchableOpacity style={styles.button} onPress = {()=> this.tomaLaFoto()}>
                                <Text>Sacar foto</Text>
                            </TouchableOpacity>
                        </View>
                    :

                    <Text>No tienes permisos para usar la CÃ¡mara</Text>
                }
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container:{
        flex:1
    },
    camara:{
      flex:7  
    },
    button:{
        flex:1,
        justifyContent:'center',
        alignItems: 'center'
    },

})

export default MyCamera
