import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'
import React, {Component} from 'react'
import { FontAwesome } from '@expo/vector-icons'
import firebase from 'firebase'
import {auth, db} from '../../firebase/config'


class Message extends Component {

    constructor(props){
        super(props)
        this.state={
            cantLikes:0,
            miLike:false,
            arrLikes:[],
            arrSubMessages:[]
        }
    }

    componentDidMount(){
        // console.log('Este es el metodo de Firebase')
        // console.log(firebase.firestore.FieldValue)
        const documento = this.props.info.data
        const tieneMiLike = documento.likes.includes(auth.currentUser.email)
        if(documento.likes){
            let cantActualLikes = documento.likes.length
            this.setState({
                likes:cantActualLikes
            })
        }
        if(tieneMiLike){
            this.setState({
                miLike: true
            })
        }
    }

    like(){
        const documento = this.props.info
        db.collection('messages').doc(documento.id).update({
            likes: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email)
        })
        .then(
            this.setState({
                miLike:true,
                cantLikes: this.state.cantLikes + 1
            })
        )
        .catch(error=> console.log(error))
    }

    unlike(){
        const documento = this.props.info
        db.collection('messages').doc(documento.id).update({
            likes: firebase.firestore.FieldValue.arrayRemove(auth.currentUser.email)
        })
        .then(
            this.setState({
                miLike:true,
                cantLikes: this.state.cantLikes - 1
            })
        )
        .catch(error=> console.log(error))
    }


    render(){
        const documento = this.props.info.data
        return (
            <View style={styles.container}>
                <View>
                    <Text style={styles.messageOwner}>{documento.owner}</Text>
                    <Text style={styles.messageText}>{documento.message}</Text>
                </View>
                <View>
                    <Text>{this.state.cantLikes}</Text>
                    {
                        this.state.miLike
                        ?

                        <TouchableOpacity>
                            <FontAwesome name='heart' size={24} color='red'/> 
                        </TouchableOpacity>
                        :
                        <TouchableOpacity onPress={()=> this.like()}>
                            <FontAwesome name='heart-o' size={24} color='black' /> 
                        </TouchableOpacity>

                    }
                </View>
            </View>
        )
    }
}

const styles= StyleSheet.create({
    container:{
        flex:1,
        flexDirection:'row',
        justifyContent:'space-between',
        paddingVertical:8,
        paddingHorizontal:6,
        borderRadius:5,
        backgroundColor:'#F5E6E8',
        marginHorizontal:10,
        marginTop:8
    },
    messageOwner:{
        fontWeight:600
    },
    messageText:{
        paddingLeft:8,
        paddingVertical:8
    }
})

export default Message
